import { faLinkedin } from "@fortawesome/free-brands-svg-icons";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  large: {
    width: theme.spacing(35),
    height: theme.spacing(35),
    marginLeft:'65px',
    [theme.breakpoints.down('xs')]: {
      marginLeft:'0px',
    },
  
  },
  mar:{
    marginLeft:'-30px',
    [theme.breakpoints.down('xs')]: {
      marginLeft:'0px',
    },
  },
  head:{
    fontSize: '80px',
      fontWeight: 800,
    [theme.breakpoints.down('xs')]: {
      fontSize: '45px',
      fontWeight: 800,
    },
  }
}));

